import { Component } from '@angular/core';

@Component({
  selector: 'sidebar-items',
  templateUrl: './sidebar-items.component.html'
})
export class SidebarItemsComponent {

  usuarioAutenticado: boolean = false;

  ngOnInit() {
    this.usuarioAutenticado = !!localStorage.getItem('username'); // Verifica si hay un usuario autenticado
      }
}
<div>
  <sidebar-item-section name="Entidades">
    <sidebar-item title="Inicio de sesion" routerLink="/">
      <img class="mr-4 h-7 w-7 rounded" src="assets/images/avatar.png" alt="Icono de hotel" style="filter: brightness(0) invert(1);"/>
    </sidebar-item>

    <sidebar-item title="Hoteles" [routerLink]="usuarioAutenticado ? '/admin/hoteles' : null">
      <img class="mr-4 h-7 w-7 rounded" [src]="usuarioAutenticado ? 'assets/images/hotelIcono.png' : 'assets/images/candado.png'" alt="Icono de hotel" style="filter: brightness(0) invert(1);"/>
    </sidebar-item>

    <sidebar-item title="Habitaciones" [routerLink]="usuarioAutenticado ? '/admin/habitaciones' : null">
      <img class="mr-4 h-7 w-7 rounded" [src]="usuarioAutenticado ? 'assets/images/habitacion.png' : 'assets/images/candado.png'" alt="Icono de hotel" style="filter: brightness(0) invert(1);"/>
    </sidebar-item>

    <sidebar-item title="Huéspedes" [routerLink]="usuarioAutenticado ? '/admin/huespedes' : null">
      <img class="mr-4 h-7 w-7 rounded" [src]="usuarioAutenticado ? 'assets/images/huesped.png' : 'assets/images/candado.png'" alt="Icono de hotel" style="filter: brightness(0) invert(1);"/>
    </sidebar-item>

    <sidebar-item title="Servicios" [routerLink]="usuarioAutenticado ? '/admin/servicios' : null">
      <img class="mr-4 h-7 w-7 rounded" [src]="usuarioAutenticado ? 'assets/images/piscina.png' : 'assets/images/candado.png'" alt="Icono de hotel" style="filter: brightness(0) invert(1);"/>
    </sidebar-item>
  </sidebar-item-section>
</div>
