import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ServicioService } from 'src/app/services/servicio.service';

@Component({
  selector: 'app-asignar-servicio',
  templateUrl: './asignar-servicio.component.html',
  styleUrls: ['./asignar-servicio.component.css']
})
export class AsignarServicioComponent {
  idServicio: number = 0;
  idHotel: number = 0;

  constructor(
    private servicioService: ServicioService,
    private router: Router
  ) {}

  asignarServicio() {
    if (this.idServicio && this.idHotel) {
      this.servicioService.asignarServicioAHotel(this.idServicio, this.idHotel).subscribe({
        next: (response) => {
          console.log(response); // Verifica la respuesta en la consola
          alert('Servicio asignado correctamente');
          this.router.navigate(['/admin/servicios']);
        },
        error: (error) => {
          console.log(error); // Verifica la respuesta en la consola
          alert('Error al asignar el servicio');
          console.error(error);
        }
      });
    } else {
      alert('Por favor, complete ambos campos.');
    }
  }
}
<div>
    <div
      class="flex w-full flex-wrap items-center justify-between rounded-2xl px-12 py-6 md:flex-nowrap"
      style="
        background-color: url('https://www.transparenttextures.com/patterns/cubes.png');
        background-image: linear-gradient(
          to right top,
          #cf4af3,
          #e73bd7,
          #f631bc,
          #fd31a2,
          #ff3a8b,
          #ff4b78,
          #ff5e68,
          #ff705c,
          #ff8c51,
          #ffaa49,
          #ffc848,
          #ffe652
        );
      "
    >
      <div>
        <h3 class="m-0 flex items-center text-lg font-medium">
          Asignar Servicio a Hotel
        </h3>
        <div class="my-4 overflow-hidden text-sm font-normal text-white">
          Complete los campos para asignar un servicio a un hotel.
        </div>
      </div>
      <img
        class="w-24 object-cover object-center md:w-44"
        src="assets/images/hotelIcono.png"
        alt=""
      />
    </div>

    <form (ngSubmit)="asignarServicio()" class="mt-6">
      <div class="grid grid-cols-2 gap-4">
        <div>
          <label for="idServicio" class="block">ID Servicio</label>
          <input
            id="idServicio"
            type="number"
            [(ngModel)]="idServicio"
            name="idServicio"
            class="w-full p-2 rounded-md bg-gray-700"
            required
          />
        </div>
        <div>
          <label for="idHotel" class="block">ID Hotel</label>
          <input
            id="idHotel"
            type="number"
            [(ngModel)]="idHotel"
            name="idHotel"
            class="w-full p-2 rounded-md bg-gray-700"
            required
          />
        </div>
      </div>

      <div class="mt-6 flex justify-center space-x-4">
        <button class="bg-red-500 text-white p-2 rounded-md" [routerLink]="['/admin/servicios']">
          Volver
        </button>
        <button type="submit" class="bg-blue-500 text-white p-2 rounded-md">
          Asignar
        </button>
      </div>
    </form>
  </div>
