import { Component, OnInit } from '@angular/core';
import { DynamicSearchDTO } from 'src/app/models/DynamicSearchDTO';
import { Servicio } from 'src/app/models/servicio.model';
import { PaginatedResponse } from 'src/app/services/huesped.service';
import { ServicioService } from 'src/app/services/servicio.service';

@Component({
  selector: 'app-servicios',
  templateUrl: './servicios.component.html',
  styleUrls: ['./servicios.component.css']
})
export class ServiciosComponent implements OnInit {
  servicios: Servicio[] = [];
  currentPage: number = 0;
  totalPages: number = 0;
  size: number = 5;


  filters = {
    id: '',
    nombre: '',
    descripcion: ''
  };
  constructor(private servicioService: ServicioService) {}

  ngOnInit(): void {
    this.obtenerServicios();
  }

  obtenerServicios(): void {
    this.servicioService.obtenerServicios(this.currentPage, this.size).subscribe(
      (response: PaginatedResponse<Servicio>) => {
        this.servicios = response.content;
        this.totalPages = response.totalPages;
      },
      error => {
        console.error('Error al obtener los servicios', error);
      }
    );
  }

  eliminarServicio(id: number): void {
    this.servicioService.eliminarServicio(id).subscribe({
      next: () => {
        this.servicios = this.servicios.filter(servicio => servicio.id !== id);
      },
      error: err => {
        console.error('Error al eliminar el servicio', err);
      }
    });
  }

  irAPagina(page: number): void {
    if (page >= 0 && page < this.totalPages) {
      this.currentPage = page;

      if (this.hayFiltrosActivos()) {
        this.obtenerServiciosConFiltros();
      } else {
        this.obtenerServicios();
      }
    }
  }


  obtenerServiciosConFiltros(): void {
    const searchRequest: DynamicSearchDTO = {
      listSearchCriteria: this.crearCriteriosBusqueda(),
      listOrderCriteria: [
        {
          sortBy: 'id',
          valuesortOrder: 'ASC'
        }
      ],
      page: {
        pageIndex: this.currentPage,
        pageSize: this.size
      }
    };

    this.servicioService.buscarServicios(searchRequest).subscribe(
      (response: PaginatedResponse<Servicio>) => {
        this.servicios = response.content;
        this.totalPages = response.totalPages;
      },
      error => {
        console.error('Error al obtener los servicios con filtros', error);
      }
    );
  }
  hayFiltrosActivos(): boolean {
    return Object.values(this.filters).some(value => value !== '');
  }

  crearCriteriosBusqueda() {
    const criteria = [];

    if (this.filters.id) {
      criteria.push({ key: 'id', value: this.filters.id, operation: 'equals' });
    }
    if (this.filters.nombre) {
      criteria.push({ key: 'nombre', value: this.filters.nombre, operation: 'like' });
    }
    if (this.filters.descripcion) {
      criteria.push({ key: 'descripcion', value: this.filters.descripcion, operation: 'like' });
    }

    return criteria;
  }

  buscarServicios(): void {
    this.currentPage = 0;  // Reiniciar la página al realizar una búsqueda
    this.obtenerServiciosConFiltros();
  }
}
<div>
  <div
    class="flex w-full flex-wrap items-center justify-between rounded-2xl px-12 py-6 md:flex-nowrap"
    style="
      background-color: url('https://www.transparenttextures.com/patterns/cubes.png');
      background-image: linear-gradient(
        to right top,
        #ff9800,
        #f57c00,
        #ef6c00,
        #e65100,
        #bf360c
      );
    "
  >
    <div>
      <h3 class="m-0 flex items-center text-lg font-medium">
        Servicios
      </h3>
      <div class="my-4 overflow-hidden text-sm font-normal text-white">
        Gestión de servicios.
      </div>
    </div>
    <img
      class="w-24 object-cover object-center md:w-44"
      src="assets/images/hotelIcono.png"
      alt="Icono de servicio"
    />
  </div>
</div>

<div class="flex justify-center mt-4 space-x-4">
  <button
    class="bg-orange-500 text-white p-2 rounded-md"
    [routerLink]="['/admin/servicios/registro']"
  >
    Registrar nuevo servicio
  </button>
  <button
    class="bg-orange-500 text-white p-2 rounded-md"
    [routerLink]="['/admin/servicios/asignar']"
  >
    Asignar servicio a hotel
  </button>
</div>
<!-- Filtros de búsqueda -->
<div class="mt-4">
  <div class="flex gap-4">
    <div>
      <label for="filterId" class="block text-sm text-white">ID</label>
      <input
        id="filterId"
        type="text"
        [(ngModel)]="filters.id"
        name="id"
        class="p-2 rounded-md bg-gray-700 text-white"
        placeholder="Buscar por ID"
      />
    </div>
    <div>
      <label for="filterNombre" class="block text-sm text-white">Nombre</label>
      <input
        id="filterNombre"
        type="text"
        [(ngModel)]="filters.nombre"
        name="nombre"
        class="p-2 rounded-md bg-gray-700 text-white"
        placeholder="Buscar por nombre"
      />
    </div>
    <div>
      <label for="filterDireccion" class="block text-sm text-white">Dirección</label>
      <input
        id="filterDireccion"
        type="text"
        [(ngModel)]="filters.descripcion"
        name="direccion"
        class="p-2 rounded-md bg-gray-700 text-white"
        placeholder="Buscar por dirección"
      />
    </div>
    <div class="flex items-end">
      <button
        (click)="buscarServicios()"
        class="bg-blue-500 text-white p-2 rounded-md"
      >
        Buscar
      </button>
    </div>
  </div>
</div>
<div class="mt-8 rounded-2xl" style="background: rgb(146 151 179 / 13%)">
  <div class="container mx-auto">
    <div class="py-8">
      <div class="py-4">
        <div class="max-w-full overflow-x-auto rounded-lg">
          <table class="w-full leading-normal text-white">
            <thead>
              <tr>
                <th class="border-b border-gray-200 px-5 py-3 text-left text-sm font-normal uppercase">
                  ID
                </th>
                <th class="border-b border-gray-200 px-5 py-3 text-left text-sm font-normal uppercase">
                  Nombre
                </th>
                <th class="border-b border-gray-200 px-5 py-3 text-left text-sm font-normal uppercase">
                  Descripción
                </th>
                <th class="border-b border-gray-200 px-5 py-3 text-left text-sm font-normal uppercase">
                  Acciones
                </th>
              </tr>
            </thead>
            <tbody *ngIf="servicios.length > 0; else noData" class="text-white">
              <tr *ngFor="let servicio of servicios">
                <td class="border-b border-gray-200 p-5 text-sm">
                  <p class="whitespace-nowrap">{{ servicio.id }}</p>
                </td>
                <td class="border-b border-gray-200 p-5 text-sm">
                  <p class="whitespace-nowrap">{{ servicio.nombre }}</p>
                </td>
                <td class="border-b border-gray-200 p-5 text-sm">
                  <p class="whitespace-nowrap">{{ servicio.descripcion }}</p>
                </td>
                <td class="border-b border-gray-200 p-5 text-sm flex gap-2">
                  <button
                    (click)="eliminarServicio(servicio.id)"
                    class="bg-red-500 text-white p-2 rounded-md"
                  >
                  <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
                  </svg>
                  </button>
                  <button
                    class="bg-orange-500 text-white p-2 rounded-md"
                    [routerLink]="['/admin/servicios/editar', servicio.id]"
                  >
                  <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 20h9m-9 0a3 3 0 01-3-3V8a3 3 0 013-3m0 0h3m-3 0a3 3 0 00-3 3v9a3 3 0 003 3m0 0h3m-3 0a3 3 0 01-3-3V8a3 3 0 013-3m0 0h3" />
                  </svg>
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
          <ng-template #noData>
            <p class="text-center text-white p-5">No hay servicios disponibles.</p>
          </ng-template>
        </div>

        <div class="flex justify-center mt-4">
          <button
            (click)="irAPagina(currentPage - 1)"
            [disabled]="currentPage === 0"
            class="bg-orange-500 text-white p-2 rounded-md"
          >
            Anterior
          </button>
          <span class="mx-2 text-white">
            Página {{ currentPage + 1 }} de {{ totalPages }}
          </span>
          <button
            (click)="irAPagina(currentPage + 1)"
            [disabled]="currentPage === totalPages - 1"
            class="bg-orange-500 text-white p-2 rounded-md"
          >
            Siguiente
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
